channelMessage:
$eval

// Initialize variables
var serverid = "$serverIcon".split("/")[4];
var messages = (messages == null) ? {} : messages;
botState.warns = (botState.warns == null) ? {} : botState.warns;
var reply = "";

// YOU CAN CHANGE THESE VARIABLES!

// Flag as spam after X messages in 5 seconds
var spam_detect = 4;

// Initialize user warns
if (botState.warns[$ID] == null)
{
    botState.warns[$ID] = {};
    botState.warns[$ID][serverid] = 0;
}

else if (botState.warns[$ID][serverid] == null) botState.warns[$ID][serverid] = 0;

// Add message to the message list
if (messages[$ID] == null || messages[$ID][serverid] == null)
{
    messages[$ID] = {};
    messages[$ID][serverid] = [Math.floor(new Date().getTime() / 1000)];
}

else messages[$ID][serverid].push(Math.floor(new Date().getTime() / 1000));

var msgcount$ID = 0;

for (var i = 0; i < messages[$ID][serverid].length; i++)
{
    if (messages[$ID][serverid][i] >= (Math.floor(new Date().getTime() / 1000)-5))
    {
        msgcount$ID += 1;
    }
}

if (msgcount$ID == spam_detect)
{
    messages[$ID][serverid] = [];
    botState.warns[$ID][serverid] += 1;

    reply = "⚠️ Don't spam, $name! Now you have " + botState.warns[$ID][serverid].toString() + " warn" + ((botState.warns[$ID][serverid] == 1) ? "!" : "s!");
}

$halt $get(reply)
